function dec(u) {
  try {
    return u?.replace(/&amp;/g, '&');
  } catch {
    return u;
  }
}

function largestPreview(p) {
  const img = p?.preview?.images?.[0];
  return dec(img?.source?.url || img?.s?.u || img?.s?.url);
}

function isImg(u) {
  return typeof u === 'string' && /(\.jpg|\.jpeg|\.png|\.gif|\.webp)(\?|$)/i.test(u || '');
}

const posts = items[0].json?.data?.children || [];
const out = [];

for (const c of posts) {
  const p = c.data || {};
  const base = {
    id: p.id,
    title: p.title,
    permalink: `https://www.reddit.com${p.permalink}`,
    chat_id: $node['Vars'].json['chat_id'],
    rate_seconds: $node['Vars'].json['rate_seconds']
  };

  if (p.is_gallery && p.media_metadata) {
    const ids = p.gallery_data?.items?.map(i => i.media_id) || Object.keys(p.media_metadata);
    const total = ids.length;
    ids.forEach((mid, idx) => {
      const m = p.media_metadata[mid];
      if (!m) return;
      const url = dec(m?.s?.u || m?.s?.gif || m?.s?.mp4 || m?.s?.url);
      if (!url) return;
      out.push({
        json: {
          ...base,
          is_video: false,
          media_type: 'image',
          media_url: url,
          slide: idx + 1,
          total
        }
      });
    });
    continue;
  }

  const rv = p?.secure_media?.reddit_video || p?.media?.reddit_video || p?.preview?.reddit_video_preview;
  if (rv?.fallback_url) {
    const v = dec(rv.fallback_url);
    let audio = null;

    // Generate audio URL from video URL
    try {
      const videoUrl = new URL(v);
      const pathParts = videoUrl.pathname.split('/');
      const filename = pathParts[pathParts.length - 1];

      // Check if it's a DASH video file
      if (filename.includes('DASH_') && (filename.endsWith('.mp4') || filename.endsWith('.webm'))) {
        // Extract quality and extension
        const match = filename.match(/DASH_(\d+)(\.[^.]+)$/);
        if (match) {
          const quality = match[1];
          const extension = match[2];

          // Construct audio URL
          const audioFilename = `DASH_audio${extension}`;
          pathParts[pathParts.length - 1] = audioFilename;
          videoUrl.pathname = pathParts.join('/');
          audio = videoUrl.toString();
        }
      }
    } catch (error) {
      // Audio URL generation failed, continue without audio
    }

    out.push({
      json: {
        ...base,
        is_video: true,
        media_type: 'reddit_video',
        media_url: v,
        audio_url: audio,
        slide: 1,
        total: 1
      }
    });
    continue;
  }

  const direct = dec(p.url_overridden_by_dest || p.url);
  if (isImg(direct)) {
    out.push({
      json: {
        ...base,
        is_video: false,
        media_type: 'image',
        media_url: direct,
        slide: 1,
        total: 1
      }
    });
    continue;
  }

  const prev = largestPreview(p);
  if (prev) {
    out.push({
      json: {
        ...base,
        is_video: false,
        media_type: 'image',
        media_url: prev,
        slide: 1,
        total: 1
      }
    });
  }
}

return out;

const state = $getWorkflowStaticData('global');
if(!state.posted) state.posted={};
const ttl=($node['Vars'].json['memory_ttl_days']||60)*24*60*60*1000;
const now=Date.now();
for(const [k,t] of Object.entries(state.posted)){
  if(!t||now-t>ttl) delete state.posted[k];
}
const fresh=[];
for(const it of items){
  const k=it.json.key||it.json.media_url||it.json.id;
  if(!k) continue;
  if(!state.posted[k]){
    state.posted[k]=now;
    fresh.push(it);
  }
}
return fresh;

return items.map(it => {
  const j = it.json;
  j.caption = j.total > 1 ? `${j.title} [${j.slide}/${j.total}]\n${j.permalink}` : `${j.title}\n${j.permalink}`;
  return { json: j };
});

// Memory-efficient data merging
const item = $input.item;

// Validate input data
if (!item || !item.json || !item.binary) {
  throw new Error('Invalid input: Missing JSON or binary data');
}

// Only pass essential data to reduce memory usage
return [{
  json: {
    id: item.json.id,
    chat_id: item.json.chat_id,
    caption: item.json.caption
  },
  binary: item.binary
}];
